import React, { Component } from "react";
import API from "../utils/API"
import Card from "../components/Card";
import Alert from "../components/Alert";
import Hero from "../components/Hero";
import Row from "../components/Row";
import Col from "../components/Col"
import Container from "../components/Container";


class Discover extends Component {
  state = {
    query: null,
    artists: []
  }

componentDidMount() {
    // do an api call
    this.loadProfiles();
  }



  loadProfiles = () => {
    API.getProfiles()
      .then(res =>
        this.setState({
          name: "",
          location: "",
          imageUrl: "",
          links: "",
          age: 0,
          instrument: "",
          style: "",
          experience: "",
          about: ""
        })
      )
      .catch(err => console.log(err));
  };

  componentDidMount() {
    this.loadProfiles();
  }
  handleInputChange = event => {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };



Query=()=> {
    return (
        <div>
            <Container style={{ marginTop: 30 }}>
                {/*Put query form in a jumbo tron */}
                <Row>
                    <Col size="md-12">
                        <QueryForm />
                    </Col>
                </Row>
                <Row>
                    <Col size="md-12">
                        {/*When user clicks this button route to Discover page*/}
                        <Link
                            to="/discover"
                            className={window.location.pathname === "/discover" ? "nav-link active" : "nav-link"}
                            >
                            <Route exact path="/discover" component={Discover} onClick={handleQuerySubmit}/>
                            Search Musicians
                            </Link>
                    </Col>
                </Row>

            </Container>
        </div>
    )
}


  render() {
    const { errors, instrument, style, location, name,imageUrl } = this.state;
    return (
      <div>

        <Hero backgroundImage="http://www.carlswebgraphics.com/backgrounds/music-notes-1280.jpg">

          <h1 className="text-center coolFont">In Your Area:</h1>


        <Container style={{ marginTop: 30 }}>
            {/*Put the card here instead of using a seperate component */}
            <Row>
              <Col size="md-12">
                <div className="car d">
                 <ul>
                   <li value ={name}></li>
                   <li value ={location}></li>
                   <li value ={instrument}></li>
                   <li value ={style}></li>
                 </ul>
                </div>

              </Col>
            </Row>

          </Container>

        </Hero>
      </div>
    );
  }
}

export default Discover;
