import React, { Component } from "react";
import API from "../utils/API"
import Card from "../components/Card";
import Alert from "../components/Alert";
import Hero from "../components/Hero";
import Row from "../components/Row";
import Col from "../components/Col"
import Container from "../components/Container";


class Discover extends Component {
  state = {
    name: "",
    location: "",
    imageUrl: "",
    links: "",
    age: "",
    instrument: "",
    style: "",
    experience: "",
    about: "",
    errors: {}
  }



  loadProfiles = () => {
    API.getProfiles()
      .then(res =>
        this.setState({
          name: "",
          location: "",
          imageUrl: "",
          links: "",
          age: 0,
          instrument: "",
          style: "",
          experience: "",
          about: ""
        })
      )
      .catch(err => console.log(err));
  };

  componentDidMount() {
    this.loadProfiles();
  }
  handleInputChange = event => {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };




  render() {
    return (
      <div>
        {/*Change this to a list of results. When user clicks a result, a modal will pop up with with the original card that includes buttons for add to a-list or decline*/}
        <Hero backgroundImage="http://www.carlswebgraphics.com/backgrounds/music-notes-1280.jpg">

          <h1 className="text-center coolFont">In Your Area:</h1>
          <h3 className="text-centerm text-white coolFont">
            Thumbs up to send to your A-List of musicians
          <br />
            Thumbs down to move on to the next
        </h3><Container style={{ marginTop: 30 }}>
            {/*Put the card here instead of using a seperate component */}
            <Row>
              <Col size="md-12">
              <div className="card">
  <img src="..." className="card-img-top" alt="..." />
  <div className="card-body">
    <h3>{API.name}</h3>

  </div>
  <ul className="list-group list-group-flush">
    <li className="list-group-item">Location: {API.location}</li>
    <li className="list-group-item">links: {API.links}</li>
    <li className="list-group-item">Age: {API.age}</li>
    <li className="list-group-item">Instrument: {API.instrument}</li>
    <li className="list-group-item">Style: {API.style}</li>
    <li className="list-group-item">Experience: {API.experience}</li>
    <li className="list-group-item">About: {API.about}</li>
  </ul>
  <div className="card-body">
  <br />
  <br />


        {/*!props.image && <i className="fa fa-spinner fa-spin" aria-hidden="true" />*/}
  </div>

</div>

              </Col>
            </Row>

          </Container>

        </Hero>
      </div>
    );
  }
}

export default Discover;
