import React from "react";
import { BrowserRouter as Router, Route } from "react-router-dom";
import Discover from "../../pages/Discover"
import { Link } from "react-router-dom";
import Query from "../../pages/Query"

//import "./style.css";

 function isValid(){
    let instrument = document.getElementById("#instrument-input").value();
    let style = document.getElementById("style-input").value(); 

    if(instrument === "Select an Instrument" && style === "Select a Style"){
        alert ("Please Select an Instrument and Style of Music ")
    }
  }

function QuerySubmitBtn(props) {
  return (
    <Link
    to="/discover"
    className={window.location.pathname === "/discover" ? "nav-link active" : "nav-link"}
     >
    <Route exact path="/discover" component={Discover} />
    Search Musicians
  </Link>




  );
}

export{
    QuerySubmitBtn, isValid
}
